import {
    CREATE_CUSTOMER,
    RETRIEVE_CUSTOMERS,
    UPDATE_CUSTOMER,
    DELETE_CUSTOMER,
    DELETE_ALL_CUSTOMERSS,
  } from "./types";

import CustomerDataService from "../services/CustomerDataService";
  

  export const retrieveCustomers = () => async (dispatch) => {
    try {
      const res = await CustomerDataService.getAll();
      dispatch({
        type: RETRIEVE_CUSTOMERS,
        payload: res.data,
      });
    } catch (err) {
      console.log(err);
    }
  };
