import React, { useState, useEffect, useCallback } from "react";

function CustomerList(props) {

    const [page, setPage] = useState(1);
    const [commitHistory, setCommitHistory] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const loadMoreCommit = () => {
        setPage(page + 1);
      };
    
      useEffect(() => {
        fetch(
          `https://api.github.com/search/commits?q=repo:facebook/react+css&page=${page}`,
          {
            method: "GET",
            headers: new Headers({
              Accept: "application/vnd.github.cloak-preview"
            })
          }
        )
          .then(res => res.json())
          .then(response => {
            setCommitHistory(response.items);
            setIsLoading(false);
          })
          .catch(error => console.log(error));
      }, [page]);


    return (
        <>
            <div className="content-wrapper">
                <section className="content-header">
                    <h1>Add New Customer</h1>
                </section>
                <section class="content">
                    <div class="row">
                        <div class="col-xs-12">
                            <div class="box">
                                <div class="box-header">
                                    <h3 class="box-title"> Customer List</h3>
                                </div>
                                <div class="box-body">
                                    <a href="#"><button class="btn btn-success"><i class="glyphicon glyphicon-plus"></i> Add Customer</button></a>
                                    <button class="btn btn-default" onClick="reload_table()"><i class="glyphicon glyphicon-refresh"></i> Reload</button>
                                    <br />
                                    <br />
                                    <table id="table" class="table table-bordered table-hover">
                                        <thead>
                                            <tr>
                                                <th width="10%">Sr. No </th>
                                                <th width="40%">Name</th>
                                                <th width="10%">Contact No.</th>
                                                <th width="10%">City</th>
                                                <th width="10%">Balance</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                        {isLoading ? (
                                            <h1>Loading...</h1>
                                            ) : (
                                                data.map((user) => (
                                                <>
                                                    <tr><td>{user.customerName}</td></tr>
                                                    <tr><td>{user.mobno}</td></tr>
                                                    <tr><td>{user.city}</td></tr>
                                                    <tr><td>{user.totalOutBal}</td></tr>
                                                </>
                                                ))
                                            )}

                                       
                                                    
                                               
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        </>
    );
}

export default CustomerList;
