import React, { useState, useEffect, useCallback } from "react";


function CustomerList(props) {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
        getPosts();
    }, []);

    // get posts
    const getPosts = () => {
        axios
            .get("https://jsonplaceholder.typicode.com/posts")
            .then((response) => {
                if (response.status === 200) {
                    setPosts(response?.data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };


    return (
        <>
            <div className="content-wrapper">
                <section className="content-header">
                    <h1>Add New Customer</h1>
                </section>
                <section class="content">
                    <div class="row">
                        <div class="col-xs-12">
                            <div class="box">
                                <div class="box-header">
                                    <h3 class="box-title"> Customer List</h3>
                                </div>
                                <div class="box-body">
                                    <a href="#"><button class="btn btn-success"><i class="glyphicon glyphicon-plus"></i> Add Customer</button></a>
                                    <button class="btn btn-default" onClick="reload_table()"><i class="glyphicon glyphicon-refresh"></i> Reload</button>
                                    <br />
                                    <br />
                                    <table id="table" class="table table-bordered table-hover">
                                        <thead>
                                            <tr>
                                                <th width="10%">Sr. No </th>
                                                <th width="40%">Name</th>
                                                <th width="10%">Contact No.</th>
                                                <th width="10%">City</th>
                                                <th width="10%">Balance</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {posts &&
                                                posts.map((post) => (
                                                    <tr
                                                        key={post?.id}
                                                        onClick={(e) =>
                                                            actionClick({ action: { type: "view" }, post, e })
                                                        }
                                                    >
                                                        <td> {post?.id} </td>
                                                        <td>{post?.customerName}</td>
                                                        <td>{post?.mobno}</td>
                                                        <td>{post?.city}</td>
                                                        <td>{post?.totalOutBal}</td>

                                                        <td>
                                                            <Link
                                                                to={"#"}
                                                                onClick={(e) =>
                                                                    actionClick({ action: { type: "edit" }, post, e })
                                                                }
                                                                title={"Edit"}
                                                            >
                                                                Edit
                                                            </Link>

                                                            <Link
                                                                to={"#"}
                                                                className="ms-3"
                                                                onClick={(e) =>
                                                                    actionClick({ action: { type: "delete" }, post, e })
                                                                }
                                                                title={"Delete"}
                                                            >
                                                                Delete
                                                            </Link>
                                                        </td>
                                                    </tr>
                                                ))}
                                        </tbody>


                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        </>
    );
}

export default CustomerList;
