import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import $ from 'jquery';
import moment  from 'moment';

function AddInvoice(props) {

    let date = new Date()

    const [customerId, setCustomerId] = useState("");
    
    const [id, setId] = useState("");
    const [customers, setCustomers] = React.useState([]);
    const [products, setProducts] = React.useState([]);
    const [invoiceNumber, setInvoiceNumber] = React.useState([]);
    const [invoiceInitial, setInvoiceInitial] = React.useState([]);
    const [invoiceNumberLabel, setInvoiceNumberLabel] = React.useState([]);
    const [amountPaid, setAmountPaid] = React.useState(0.00);
    const [amountDue, setAmountDue] = React.useState(0.00);
    const [itemTotal, setItemTotal] = React.useState(0.00);
    const [itemTax, setItemTax] = React.useState(0.00);
    const [billTotal, setBillTotal] = React.useState(0.00);
    const [roundValue, setRoundValue] = React.useState(0.00);
    const [invoiceTotal, setInvoiceTotal] = React.useState(0.00);
    const [totalOutBal, setTotalOutBal] = React.useState(0.00);

    /* Table column */
    const [rate, setRate] = React.useState(0.00);
    const [ratePre, setRatePre] = React.useState(0.00);
    const [quantity, setQuantity] = React.useState(1);
    const [quantityPre, setQuantityPre] = React.useState(0);
    const [productId, setProductId] = useState("");
    const [rowCounter, setRowCounter] = useState(1);
    const [hsnCode, setHsnCode] = useState("");
    const [description, setDescription] = useState("");
    const [gst, setGst] = useState(0.00);
    const [gstPre, setGstPre] = useState(0.00);
    const [gstAmt, setGstAmt] = useState(0.00);
    const [gstAmtPre, setGstAmtPre] = useState(0.00);
    const [actionFlag, setActionFlag] = useState("");

    
    React.useEffect(() => {
        getInvoiceInitial();
        getInvoiceNumber();
        getCuctomers();
        getProducts();

    }, []);

        async function getCuctomers() {
          const response = await fetch("http://localhost:9191/api/invoices/customers");
          const body = await response.json();
          setCustomers(body.map(customer => {
              return { value: customer.id, label: customer.name };
            }));
        }
        

        async function getProducts() {
            const response = await fetch("http://localhost:9191/api/invoices/products");
            const body = await response.json();
            setProducts(body.map(product => {
                return { value: product.id, label: product.name };
              }));
          }
         

          const getInvoiceNumber = () => {
            axios
                .get("http://localhost:9191/api/invoices/invoiceNoSeq")
                .then((response) => {
                    if (response.status === 200) {
                        setInvoiceNumber(response?.data);
                    }
                })
                .catch((error) => {
                    console.log(error);
                });
        };


        const getInvoiceInitial = () => {
            axios
                .get("http://localhost:9191/api/invoices/initial")
                .then((response) => {
                    if (response.status === 200) {
                        setInvoiceInitial(response?.data);
                    }
                })
                .catch((error) => {
                    console.log(error);
                });
        };

        function handleRateChange(event) {
            event.preventDefault()
            setRate(event.target.value);

            /* Calculate Taxable Amt */
           let taxableAmount = event.target.value * quantity;
           let taxableAmounPre = ratePre * quantityPre;
           let itemTotal = taxableAmount - taxableAmounPre;
           setItemTotal(itemTotal);
           let singleItemTax = (parseFloat(taxableAmount) / 100) * parseFloat(gst);
           let singleItemTaxPre = (parseFloat(taxableAmounPre) / 100) * parseFloat(gstPre);
           let itemTaxValue = (itemTax - singleItemTaxPre) + parseFloat(singleItemTax);
           setItemTax(itemTaxValue);
           let billTotal =  parseFloat(itemTotal)  + parseFloat(itemTaxValue);
           setBillTotal(billTotal);
           setInvoiceTotal(billTotal);
        }


        function handleRateChange(event) {
            event.preventDefault()
            setRate(event.target.value);

            /* Calculate Taxable Amt */
           let taxableAmount = event.target.value * quantity;
           let taxableAmounPre = ratePre * quantityPre;
           let itemTotal = taxableAmount - taxableAmounPre;
           setItemTotal(itemTotal);
           let singleItemTax = (parseFloat(taxableAmount) / 100) * parseFloat(gst);
           let singleItemTaxPre = (parseFloat(taxableAmounPre) / 100) * parseFloat(gstPre);
           let itemTaxValue = (itemTax - singleItemTaxPre) + parseFloat(singleItemTax);
           setItemTax(itemTaxValue);
           let billTotal =  parseFloat(itemTotal)  + parseFloat(itemTaxValue);
           setBillTotal(billTotal);
           setInvoiceTotal(billTotal);
        }


        function sayHello(name) {
            alert(`hello, ${name}`);
            
            var totalRows        = $('#totalRows').val();
   	        var currentRowCount  = $('#currentRowCount').val();

            let  tr = $('#editable-sample').find('tr').last(); // $(this).closest('.tr_clone');
            let clone = clone_pre_row(tr);
            var rowCounter = $('#rowCounter').val();
   
            $('#totalRows').val(++ totalRows);
   		    $('#currentRowCount').val(++ currentRowCount);
   
   		$('.cancelBtn').click(function () {
   
   			var numrows1 = $('table#editable-sample tr').length;
   			if (numrows1 == 2) {
   				$("#myModal").modal('show');
   				return false;
   			}
   
   			var invoiceDetails = getInvoiceDetailsColumn(clone);
   
   			/* Calculate Taxable Amt */
   			var taxableAmount = get_taxable_amt(invoiceDetails['rate'].val(), invoiceDetails['quantity'].val());
   
   			/* Calculate Previuos Taxable Amt */
   			var taxableAmounPre = get_taxable_amt(invoiceDetails['ratePre'].val(), invoiceDetails['quantityPre'].val());
   
   			/* Calculate Item Total */
   			var item_total_value = get_item_total_cancel(taxableAmount);
   			$('#itemTotal').val(item_total_value);
   
   			/* calculat Single Item Tax */
   			var single_item_tax_value = get_single_item_tax(taxableAmount, invoiceDetails['gst'].val());
   			invoiceDetails['gstAmt'].val(single_item_tax_value);
   
   			/* calculat Item Tax */
   
   			var item_tax_value = get_item_tax_cancel(single_item_tax_value);
   			$('#itemTax').val(parseFloat(item_tax_value));
   
   			var bill_total_val = get_bill_total(item_total_value, item_tax_value);
   			$('#billTotal').val(parseFloat(bill_total_val));
   
   
   			$('#roundValue').val((Math.ceil(parseFloat(bill_total_val)) - parseFloat(bill_total_val)).toFixed(2));
   			$('#invoiceTotal').val(Math.ceil(parseFloat(bill_total_val)));
   			$('#amountPaid').val($('#invoiceTotal').val());
   			$('#amountDue').val(0.00);
   			clone.remove();

   			$('#totalRows').val(-- totalRows);
   			$('#currentRowCount').val(-- currentRowCount);
   		});

        
           function get_taxable_amt(rateval, quantityval) {
            var taxableamount = parseFloat(rateval) * parseFloat(quantityval);
            return taxableamount;
        }
        
        function get_item_total(taxableamount, taxableamountpre) {
            var item_total_val = ((parseFloat($('#itemTotal').val()) - taxableamountpre) + parseFloat(taxableamount));
            return item_total_val;
        }
        
        function get_single_item_tax(taxableamount, gst_rate) {
            var single_item_tax_value = (parseFloat(taxableamount) / 100) * parseFloat(gst_rate);
            return single_item_tax_value;
        }
        
        function get_item_tax(single_item_tax_value, old_single_item_tax_value) {
            var item_tax_val = ((parseFloat($('#itemTax').val()) - old_single_item_tax_value) + parseFloat(single_item_tax_value));
            return item_tax_val;
        }
        
        function get_bill_total(item_total_value , item_tax_value) {
            var bill_total_val = 0.00;
            //var gst_included_value = $('#is_gst_included').val();
            var gst_included_value = $("input[type='radio'][name='is_gst_included']:checked").val();
            if (gst_included_value == 'N') {
                bill_total_val = parseFloat(item_total_value) + parseFloat(item_tax_value);
            } else {
                bill_total_val = parseFloat(item_total_value);
            }
            return bill_total_val;
        }
        
        function set_previous_values(rateval, gstval, invoiceDetails) {
            invoiceDetails['ratePre'].val(rateval);
            invoiceDetails['gstPre'].val(gstval);
        }
        
        function get_item_total_cancel(taxableamount) {
            var item_total_val = parseFloat($('#itemTotal').val()) - parseFloat(taxableamount);
            return item_total_val;
        }
        
        function get_item_tax_cancel(gstAmt) {
            var item_tax_val = parseFloat($('#itemTax').val()) - parseFloat(gstAmt);
            return item_tax_val;
        }
        
        function click_add_button() {
        
            var currentRowCount = $('#currentRowCount').val();
            $('.items').select2("destroy"); 
            /*
            if(currentRowCount == 1)       
            {
                $tr =$('#editable-sample').find('tbody').first();// $(this).closest('.tr_clone');
            }
            else{
                $tr =$('#editable-sample').find('tr').last();// $(this).closest('.tr_clone');
            }*/
        
            tr =$('#editable-sample').find('tr').last();// $(this).closest('.tr_clone');
            //var clone = $tr.clone().appendTo("#editable-sample");
            
            return clone;
        }
        
        function clone_pre_row(tr) {
        
            var totalRows = $('#totalRows').val();
            var currentRowCount = $('#currentRowCount').val();
        
            var clone = tr.clone().appendTo("#editable-sample");
        
            var rowcounter = ++ currentRowCount;
            $('.items').select2();
            //clone.find('#productId').select2('val', '-1');
            clone.find('input').val('');
            clone.find('#rate').val('0.00');
            clone.find('#ratePre').val('0.00');
            clone.find('#quantity').val('1.00');
            clone.find('#quantityPre').val('0.00');
            clone.find('#taxable').val('0.00');
            clone.find('#taxablePre').val('0.00');
            clone.find('#gst').val('0.00');
            clone.find('#gstPre').val('0.00');
            clone.find('#gstAmt').val('0.00');
            clone.find('#gstAmtPre').val('0.00');
            clone.find('#rowCounter').val(rowcounter);
            clone.find('#hiddenCountVal').val(rowcounter);
            clone.find('#actionFlag').val('A');
            clone.find('.cancelBtn').attr('id', 'cancelBtn' + rowcounter);
        
            $('#totalRows').val(++totalRows);
            $('#currentRowCount').val(rowcounter);
        
            return clone;
        }
        
        function getInvoiceDetailsColumn(jrow)
        {
            var invoiceDetails = {
        
                productId		: jrow.find('#productId'),
                item			: jrow.find('#item'),
                hsnCode			: jrow.find('#hsnCode'),
                preProductId	: jrow.find('#preProductId'),
                description		: jrow.find('#description'),
                rate			: jrow.find('#rate'),
                ratePre			: jrow.find('#ratePre'),
                quantity		: jrow.find('#quantity'),
                quantityPre		: jrow.find('#quantityPre'),
                taxable			: jrow.find('#taxable'),
                taxablePre		: jrow.find('#taxablePre'),
                gst				: jrow.find('#gst'),
                gstPre			: jrow.find('#gstPre'),
                gstAmt			: jrow.find('#gstAmt'),
                gstAmtPre		: jrow.find('#gstAmtPre'),
                rowCounter		: jrow.find('#rowCounter'),
                actionFlag		: jrow.find('#actionFlag'),
            };
        
            return invoiceDetails;
        }


        
    }


    return (
        <div>

            <div className="content-wrapper">
                <section className="content-header">
                    <h1>
                        <div className="col-xs-12">
                            Add Invoice
                        </div>
                    </h1>
                </section>
                <section className="invoice">
                    <form method="POST" action="" id="frm_submit">
                        <div id="myModal" className="modal fade" data-keyboard="false" data-backdrop="static">
                            <div className="modal-dialog">
                                <div className="modal-content">
                                    <div className="modal-body">
                                        <div className="alert alert-danger">
                                            <h4><i className="icon fa fa-ban"></i> Alert!</h4>
                                            <strong>Error!</strong> There Must Be Atleast One Row In Table, You Cannot Delete All Rows
                                        </div>
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" id="dismiss" data-dismiss="modal" className="btn btn-primary">Ok</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-xs-3">
                                <small>Customer Name</small>
                                <address>
                                    <div className="form-group">
                                        <select className='form-control select2' value={customerId} onChange={(e) => setCustomerId(e.target.value)}>
                                            {customers.map(o => (
                                                <option key={o.value} value={o.value}>{o.label}</option>
                                            ))}
                                        </select>
                                        <input className="form-control input-sm" type="hidden" name="customer_state_id" id="customer_state_id" value="27" placeholder="Total Paid" />
                                        <input className="form-control input-sm" type="hidden" id="client_id" name="client_id" />
                                        <input className="form-control input-sm" type="hidden" id="invoice_type" name="invoice_type" value="P" />
                                        <input className="form-control input-sm" type="hidden" name="name" id="name" placeholder="Name" value="" />
                                        <input className="form-control" type="text" id="invoiceNumber" name="invoiceNumber" value={invoiceNumber} onChange={(e) => setInvoiceNumber(e.target.value)} />
                                        <input className="form-control" type="hidden" id="receipt_no" name="receipt_no" value="" />
                                        <input className="form-control" type="hidden" id="receipt_name" name="receipt_name" value="" />
                                        <input className="form-control" type="text" id="invoiceInitial" name="invoiceInitial" value={invoiceInitial} onChange={(e) => setInvoiceInitial(e.target.value)} />
                                        <input className="form-control input-sm" type="hidden" name="customer_gstin" id="customer_gstin" placeholder="Enter GSTIN If Available " />
                                        <input className="form-control input-sm" type="hidden" name="totalOutBal" id="totalOutBal" placeholder="Current Out Balance" value={totalOutBal} vonChange={(e) => setTotalOutBal(e.target.value)} />
                                        <input className="form-control input-sm" type="hidden" name="total_amt_paid" id="total_amt_paid" placeholder="Total Paid" value="0.00" />
                                        <input type="hidden" id="totalRows" name="totalRows" value="1" />
                                        <input type="hidden" id="currentRowCount" name="currentRowCount" value="1" />
                                    </div>
                                </address>
                            </div>
                            <div className="col-xs-1">
                            </div>
                            <div className="col-xs-2">
                                <small>GST Included </small><br />
                                <label id="gstlabel">
                                    <input type="radio" className="flat-red" name="is_gst_included" id="gst_included_yes" value="Y" /> &nbsp;&nbsp;
                                    Yes &nbsp;&nbsp;&nbsp;&nbsp;
                                    <input type="radio" className="flat-red" name="is_gst_included" id="gst_included_no" value="N" checked={true} /> &nbsp;&nbsp;
                                    No
                                </label>
                            </div>
                            <div className="col-xs-2">
                                <small> Invoice Number</small>
                                <input className="form-control" type="text" name="invoiceNumberLabel" id="invoiceNumberLabel" value={invoiceInitial +''+ invoiceNumber} onChange={(e) => setInvoiceNumberLabel(e.target.value)} width="100" />
                            </div>
                            <div className="col-xs-2">
                                <small className="pull-right">Invoice Date: <input type="text" className="form-control pull-right" id="invoice_date" name="invoice_date" required placeholder="Enter Invoice Date" value={moment(new Date()).format("DD/MM/YYYY")} /></small>
                                <input className="form-control" type="hidden" name="business_state_id" id="business_state_id" value="" />
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-xs-12 table-responsive">
                                <table className="table table-striped table-bordered dataTable" id="editable-sample" aria-describedby="editable-sample_info">
                                    <tbody>
                                        <tr>
                                            <th width="5%">S.No</th>
                                            <th width="20%">Product</th>
                                            <th width="30%">Description</th>
                                            <th width="10%">Rate</th>
                                            <th width="10%">Quantity</th>
                                            <th width="10%">Gst %</th>
                                            <th width="10%">GST Amt</th>
                                            <th width="5%">Delete</th>
                                        </tr>

                                        <tr className="tr_clone">
                                            <td className="sorting_1">
                                                <input type="text" name="rowCounter[]" className="rowCounter" id="rowCounter" value={rowCounter} onChange={(e) => setRowCounter(e.target.value)}  />
                                            </td>
                                            <td className="" >
                                            <select className='form-control select2' value={productId} onChange={(e) => setProductId(e.target.value)} >
                                                {products.map(o => (
                                                    <option key={o.value} value={o.value}>{o.label}</option>
                                                ))}
                                            </select>
                                                <input type="hidden" name="hsnCode[]" className="form-control input-sm smallsmallnumber" id="hsnCode" value={hsnCode} onChange={(e) => setHsnCode(e.target.value)} />
                                            </td>
                                            <td className=""><input type="text" className="form-control input-sm number" placeholder="Description" name="description[]" id="description" value={description} onChange={(e) => setDescription(e.target.value)} /></td>
                                            <td className=""> <input type="text" required="" className="form-control input-sm smallnumber" placeholder="Rate" name="rate[]" id="rate" value={rate}   onChange={handleRateChange}    /><input type="hidden" name="ratePre[]" id="ratePre" value={ratePre} onChange={(e) => setRatePre(e.target.value)} /></td>
                                            <td className=""><input type="text" required="" className="form-control input-sm smallsmallnumber" placeholder="Quantity" name="quantity[]" value={quantity} onChange={(e) => setQuantity(e.target.value)} /><input type="hidden" name="quantityPre[]" id="quantityPre" value={quantityPre} onChange={(e) => setQuantityPre(e.target.value)} /></td>
                                            <td className="">  <input type="text" className="form-control input-sm smallnumber" placeholder="GST %" name="gst[]" id="gst" value={gst} onChange={(e) => setGst(e.target.value)} /><input type="hidden" className="form-control" placeholder="GST %" name="gstPre[]" id="gstPre" value={gstPre} onChange={(e) => setGstPre(e.target.value)} /></td>
                                            <td className=""><input type="text" className="form-control input-sm smallnumber" placeholder="GST Amt" name="gstAmt[]" id="gstAmt" value="0.00" /><input type="hidden" className="form-control input-sm smallsmallnumber" placeholder="GST Amount" name="gstAmtPre[]" id="gstAmtPre" value="0.00" />
                                                <input type="hidden" name="actionFlag[]" className="form-control input-sm smallsmallnumber" id="actionFlag" value="A" /></td>
                                            <td className=""><button type="button" className="btn btn-danger cancelBtn" name="cancelBtn[]" id="cancelBtn"><i className="fa fa-trash-o"></i></button></td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-xs-2">
                                <br /><br />
                                <button type="button" id="addmore" onClick={() => sayHello('James')} className="btn btn-primary" >
                                    <i className="fa fa-plus"></i> Add Row
                                </button>
                            </div>
                            <div className="col-xs-5">
                                <br />
                                <div className="box box-info">
                                    <div className="form-horizontal">
                                        <div className="box-body">
                                            <div className="form-group" id="amount_paid_div">
                                                <label  className="col-sm-4 control-label">Amount Paid</label>
                                                <div className="col-sm-8">
                                                <input type="text" className="form-control" name="amountPaid" id="amountPaid" placeholder="Amount Paid" value={amountPaid} onChange={(e) => setAmountPaid(e.target.value)} />
                                                    <input type="hidden" className="form-control input-sm number" name="payment_voucher" placeholder="Payment Voucher" id="payment_voucher" value="" />
                                                    <input type="hidden" className="form-control input-sm number" name="payment_number" id="payment_number" placeholder="Payment Number" value="" />
                                                </div>
                                            </div>
                                            <div className="form-group" id="amount_due_div">
                                                <label  className="col-sm-4 control-label">Amount Due</label>
                                                <div className="col-sm-8">
                                                    <input type="text" className="form-control" name="amountDue" id="amountDue" placeholder="Amount Due" value={amountDue} onChange={(e) => setAmountDue(e.target.value)} />
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xs-5">
                                <div className="table-responsive">
                                    <table className="table">
                                        <tbody>
                                            <tr>
                                                <th >Item Total Amount:</th>
                                                <td><input type="text" id="itemTotal" name="itemTotal" value={itemTotal} onChange={(e) => setItemTotal(e.target.value)}  />&nbsp;&nbsp;&nbsp;Rs.
                                                </td>
                                            </tr>
                                            <tr>
                                                <th>Item Total Tax :</th>
                                                <td>
                                                    <input type="text" id="itemTax" name="itemTax"  value={itemTax} onChange={(e) => setItemTax(e.target.value)}  />&nbsp;&nbsp;&nbsp;Rs.
                                                </td>
                                            </tr>
                                            <tr>
                                                <th >Billled Amount:</th>
                                                <td><input type="text" id="billTotal" name="billTotal"  value={billTotal} onChange={(e) => setBillTotal(e.target.value)} />&nbsp;&nbsp;&nbsp;Rs.
                                                </td>
                                            </tr>
                                            <tr>
                                                <th>Round Value:</th>
                                                <td><input type="text" id="roundValue" name="roundValue"  value={roundValue} onChange={(e) => setRoundValue(e.target.value)} />&nbsp;&nbsp;&nbsp;%
                                                </td>
                                            </tr>
                                            <tr>
                                                <th>Grand Total (Net Amount):</th>
                                                <td><input type="text" id="invoiceTotal" name="invoiceTotal"  value={invoiceTotal} onChange={(e) => setInvoiceTotal(e.target.value)} />&nbsp;&nbsp;&nbsp;Rs.
                                                </td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                        <div className="row no-print">
                            <div className="col-xs-12">
                                <input type="submit" className="btn btn-success pull-right" value="Save Payment" />
                            </div>
                        </div>
                    </form>
                </section>
                <div className="clearfix"></div>
            </div>

        </div>
    );

    $(function() {

        $('#addmore').click(function() {
            alert(111)
        });

        
      });

    
}

export default AddInvoice;
