import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CustomerDataService from '../../services/CustomerDataService';
import {Link, Routes, Route, useParams, useNavigate} from 'react-router-dom'

function EditCustomer(props) {

    const { customerId }= useParams();
    let navigate = useNavigate();

    const [id, setId] = useState("");
    const [name, setCustomerName] = useState("");
    const [contactNo, setMobno] = useState("");
    const [address, setAddress] = useState("");
    const [city, setCity] = useState("");
    const [pin, setPin] = useState("");
    const [email, setEmail] = useState("");
    const [stateId, setStateId] = useState("");
    const [pan, setPan] = useState("");
    const [totalOutBal, setTotalOutBal] = useState("");


    const [currentTutorial, setCurrentTutorial] = useState(initialTutorialState);
    const [message, setMessage] = useState("");

    const getTutorial = id => {
        TutorialDataService.get(id)
        .then(response => {
            setCurrentTutorial(response.data);
            console.log(response.data);
        })
        .catch(e => {
            console.log(e);
        });
    };

  useEffect(() => {
    if (id)
      getTutorial(id);
  }, [id]);

    useEffect(() => {
        setId(localStorage.getItem('iD'));
        setCustomerName(localStorage.getItem('name'));
        setMobno(localStorage.getItem('contactNo'));
        setAddress(localStorage.getItem('address'));
        setCity(localStorage.getItem('city'));
        setPin(localStorage.getItem('pin'));
        setEmail(localStorage.getItem('email'));
        setStateId(localStorage.getItem('stateId'));
        setPan(localStorage.getItem('pan'));
        setTotalOutBal(localStorage.getItem('totalOutBal'));
        
    }, []);

    const saveCustomer = () => {
        var data = {
            name : name,
            contactNo : contactNo,
            address : address,
            city : city,
            pin : pin,
            email : email,
            stateId : stateId,
            pan : pan,
            totalOutBal : totalOutBal,
        };
        CustomerDataService.create(data)
          .then(response => {
            if (response.ok) {
                console.log(response.data);
                navigate('/customers');
            }
          })
          .catch(e => {
            console.log(e);
          });
    };

    return (
        <>
            <div className="content-wrapper">
                <section className="content-header">
                    <h1>Add New Customer</h1>
                </section>
                <section className="content">
                    <div className="row">
                        <div className="col-xs-12">
                            <div className="box">
                                <div className="box-header">
                                    <h3 className="box-title"> Customer Master</h3>
                                </div>
                                <div className="box-body">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group required">
                                                <label className="control-label">Customer Name</label>
                                                <input type="text" className="form-control" name="name" value={name} onChange={(e) => setCustomerName(e.target.value)} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group required">
                                                <label className="control-label">Contact Number</label>
                                                <input type="text" className="form-control" name="contactNo" value={contactNo} onChange={(e) => setMobno(e.target.value)} />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group required">
                                                <label className="control-label">Address</label>
                                                <input type="text" className="form-control" name="address" value={address}  onChange={(e) => setAddress(e.target.value)} />
                                            </div>
                                        </div>
                                        <div className="col-md-3">
                                            <div className="form-group required">
                                                <label className="control-label">City</label>
                                                <input type="text" className="form-control" name="city" value={city} onChange={(e) => setCity(e.target.value)} />
                                            </div>
                                        </div>
                                        <div className="col-md-3">
                                            <div className="form-group">
                                                <label>Pin</label>
                                                <input type="text" className="form-control" name="pin" value={pin} onChange={(e) => setPin(e.target.value)} />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>State</label>
                                                <input type="text" className="form-control" name="stateId" value={stateId} onChange={(e) => setStateId(e.target.value)} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group required">
                                                <label>Email</label>
                                                <input type="text" className="form-control" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>PAN</label>
                                                <input type="text" className="form-control" name="pan" value={pan}  onChange={(e) => setPan(e.target.value)} />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group required">
                                                <label>Current Outstanding Balance</label>
                                                <input type="text" className="form-control" name="totalOutBal" value={totalOutBal} onChange={(e) => setTotalOutBal(e.target.value)} />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-md-3">
                                        </div>
                                        <div className="col-md-3">
                                            <button type="submit" className="btn btn-success btn-block btn-flat r-btn" onClick={saveCustomer}>Save</button>
                                        </div>
                                        <div className="col-md-6">
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        </>
    );
}

export default EditCustomer;
